import React, { Component } from 'react';
import { render }           from 'react-dom';
import $ from "jquery";
import axios from 'axios';
import swal from 'sweetalert';
import SimpleReactValidator from 'simple-react-validator';
import CreateUser       from './CreateUser.js';
import IAssureTableUM from '../../TGKIAssureTableUM/IAssureTable.jsx';
import UMDelRolRow from './UMDelRolRow.jsx';
import UMAddRolRow from './UMAddRolRow.jsx';
import UMSelectRoleUsers from './UMSelectRoleUsers.jsx';


axios.defaults.baseURL = 'http://cofficapi.iassureit.com/';
// axios.defaults.baseURL = 'http://localhost:5012/';
// axios.defaults.headers.common['Authorization'] = AUTH_TOKEN;
axios.defaults.headers.post['Content-Type'] = 'application/json';



class UMListOfUsers2 extends Component{

     constructor(props) {
    super(props);
    this.state = {
            allPosts : [],
            "twoLevelHeader"    : {
                apply           : false,
            },
             "tableHeading"     : {

                fullName        : 'User Name',
                emailId       : 'Email',
                mobileNumber    : 'Mobile Number', 
                status          : 'Status',
                roles         : 'Role',
                actions         : 'Action',
            },
            "startRange"        : 0,
            "limitRange"        : 10, 

            blockActive     : "all",

            adminRolesListData   : [

              { roleName : "vendor"},
              { roleName : "admin"},
              { roleName : "user"},            

            ],

            checkedUser  : [],
            activeswal : false,
            blockswal : false,
            confirmDel : false,
            unCheckedUser : [],
    };

    // this.handleChange = this.handleChange.bind(this);
    // this.getSubscriptionList = this.getSubscriptionList.bind(this);
  }
  handleChange(event){
      event.preventDefault();
        const target = event.target;
        const name   = target.name;  
    }
 componentDidMount() {
      var data = {
      "startRange"        : this.state.startRange,
            "limitRange"        : this.state.limitRange, 
    }
    axios.get('/api/users/get/list/1')
    .then( (res)=>{      
      // console.log("herer=========================>>>>>>>>>>",res.data);

      var tableData = res.data.map((a, i)=>{
        return {
          _id       : a._id,
          fullName        : a.fullName ? a.fullName : "-",
                  email       : a.emailId ? a.emailId : "-",
                  mobileNumber    : a.mobileNumber ? a.mobileNumber : "-", 
                  status          : a.status ? a.status : "-",  
                  roles       : a.roles ? a.roles : "-",
                  checked        : false,
        }
      })
      this.setState({
            completeDataCount : res.data.length,
            tableData     : tableData,          
          },()=>{
            console.log('tableData', this.state.tableData);
          })
    })
    .catch((error)=>{
      // console.log("error = ",error);
      // alert("Something went wrong! Please check Get URL.");
        if(error.message === "Request failed with status code 401")
              {
                   swal("Your session is expired! Please login again.","", "error");
                   this.props.history.push("/");
              }
    });
    this.getData(this.state.startRange, this.state.limitRange)
  }
    getData(startRange, limitRange){    
    var data = {
      "startRange"        : startRange,
            "limitRange"        : limitRange, 
    } 
       axios.get('/api/users/get/list/1')
        .then( (res)=>{  
          console.log('res ashish',res.data)
          var tableData = res.data.map((a, i)=>{
        return {
          _id       : a._id,
          fullName        : a.fullName ? a.fullName : "-",
                  email       : a.emailId ? a.emailId : "-",
                  mobileNumber    : a.mobileNumber ? a.mobileNumber : "-", 
                  status          : a.status ? a.status : "-",  
                  roles       : a.roles ? a.roles : "-",
                  checked        : false,
        }
      })
          // console.log('res============', res.data);
            this.setState({
              completeDataCount : res.data.length,
              tableData     : tableData,          
            },()=>{
            })
        })
      .catch((error)=>{
        console.log("error = ",error);
          if(error.message === "Request failed with status code 401")
              {
                   swal("Your session is expired! Please login again.","", "error");
                   this.props.history.push("/");
              }
        // alert("Something went wrong! Please check Get URL.");
      }); 
    }
    getSearchText(searchText, startRange, limitRange){
        // console.log(searchText, startRange, limitRange);
        this.setState({
            tableData : []
        });
    }

    adminRolesListData(){
    // return  Meteor.roles.find({"name":{ $nin: ["superAdmin"] }}).fetch();
  }


  adminUserActions(event){
      event.preventDefault();
      var checkedUsersList     = this.state.checkedUser;
      // console.log('id array here', checkedUsersList);

      if( checkedUsersList.length > 0 ){
        var selectedValue        = this.refs.userListDropdown.value;
        var keywordSelectedValue = selectedValue.split('$')[0];
        var role                 = selectedValue.split('$')[1];
        // console.log("selectedValue",selectedValue);
        // console.log("role",role);
        // console.log("keywordSelectedValue",keywordSelectedValue);

        switch(keywordSelectedValue){
          case '-':
            // console.log('selectedValue:' + selectedValue);
            break;

          case 'block_selected':

          for(var i=0;i< checkedUsersList.length;i++)
          {
            var selectedId = checkedUsersList[i];
            var formValues ={
              userID : selectedId,
              status : 'Blocked',
            }
            // console.log("selected i",selectedId);
             axios
              .post('/api/users/post/statusaction',formValues)
              .then(
                (res)=>{
                  // console.log('res', res);
                  this.setState({
                    blockswal : true,
                    checkedUser : null,

                  })


                  checkedUsersList = null;
                  // this.props.history.push('/umlistofusers');

                    // update table here
                      var data = {
                      "startRange"        : this.state.startRange,
                            "limitRange"        : this.state.limitRange, 
                    }
                    axios.post('/api/users/post/userslist', data)
                    .then( (res)=>{      
                      // console.log("herer",res);
                      var tableData = res.data.map((a, i)=>{
                        return {
                          _id       : a._id,
                          fullName        : a.fullName ? a.fullName : "-",
                                  emailId       : a.emailId ? a.emailId : "-",
                                  mobileNumber    : a.mobileNumber ? a.mobileNumber : "-", 
                                  status          : a.status ? a.status : "-",  
                                  roles       : a.roles ? a.roles : "-",
                                  checked        : false,
                        }
                      })
                      this.setState({
                            completeDataCount : res.data.length,
                            tableData     : tableData,          
                          },()=>{
                            // console.log('tableData', this.state.tableData);
                          })
                    })
                    .catch((error)=>{
                      // console.log("error = ",error);
                      // alert("Something went wrong! Please check Get URL.");
                        if(error.message === "Request failed with status code 401")
                          {
                               swal("Your session is expired! Please login again.","", "error");
                               this.props.history.push("/");
                          }
                    });



                }).catch((error)=>{ 

                    if(error.message === "Request failed with status code 401")
                      {
                           swal("Your session is expired! Please login again.","", "error");
                           this.props.history.push("/");
                      }

                // console.log("error = ",error);
              });

           }  


            if(this.state.blockswal == true)
            {
               swal("Account blocked successfully","","success");
            }
            break;

          case 'active_selected':

            for(var i=0;i< checkedUsersList.length;i++)
          {
            var selectedId = checkedUsersList[i];
            var formValues ={
              userID : selectedId,
              status : 'Active',
            }
            // console.log("selected i",selectedId);

             axios
              .post('/api/users/post/statusaction',formValues)
              .then(
                (res)=>{
                  // console.log('res', res);
                  this.setState({
                    activeswal : true,
                    checkedUser : null,
                  });

                  checkedUsersList = null;

                    // update table here
                      var data = {
                      "startRange"        : this.state.startRange,
                            "limitRange"        : this.state.limitRange, 
                    }
                    axios.post('/api/users/post/userslist', data)
                    .then( (res)=>{      
                      // console.log("herer",res);
                      var tableData = res.data.map((a, i)=>{
                        return {
                          _id       : a._id,
                          fullName        : a.fullName ? a.fullName : "-",
                                  emailId       : a.emailId ? a.emailId : "-",
                                  mobileNumber    : a.mobileNumber ? a.mobileNumber : "-", 
                                  status          : a.status ? a.status : "-",  
                                  roles       : a.roles ? a.roles : "-",
                                  checked        : false,
                        }
                      })
                      this.setState({
                            completeDataCount : res.data.length,
                            tableData     : tableData,          
                          },()=>{
                            // console.log('tableData', this.state.tableData);
                          })
                    })
                    .catch((error)=>{
                      // console.log("error = ",error);
                      // alert("Something went wrong! Please check Get URL.");
                        if(error.message === "Request failed with status code 401")
                          {
                               swal("Your session is expired! Please login again.","", "error");
                               this.props.history.push("/");
                          }
                    });


                }).catch((error)=>{ 

                // console.log("error = ",error);

                  if(error.message === "Request failed with status code 401")
                    {
                         swal("Your session is expired! Please login again.","", "error");
                         this.props.history.push("/");
                    }
              });

           }  

             if(this.state.activeswal == true)
             {
               swal("Account activated successfully","","success");
             }
            break;

          case 'cancel_selected':

           $('#deleteModal').addClass("in");
           $('#deleteModal').css("display","block");

          break;

          case 'add':

           for(var i=0;i< checkedUsersList.length;i++)
          {
            var selectedId = checkedUsersList[i];
            var formValues ={
              userID : selectedId,
              roles   : role,
            }
            // console.log("selected i",selectedId);

             axios
              .post('/api/users/post/roleadd/',formValues)
              .then(
                (res)=>{
                  console.log('res----------------', res);
                  if(res.data.message =="Role is already exists")
                  {
                    swal("Role is already assigned","","error");
                    this.setState({
                  unCheckedUser : []
              })
                  }
                  else{
              swal("Assigned Role Added Successfully","","success");
                    checkedUsersList = null;

                      // update table here
                      var data = {
                      "startRange"        : this.state.startRange,
                            "limitRange"        : this.state.limitRange, 
                    }
                    axios.post('/api/users/post/userslist', data)
                    .then( (res)=>{      
                      // console.log("herer",res);
                      var tableData = res.data.map((a, i)=>{
                        return {
                          _id       : a._id,
                          fullName        : a.fullName ? a.fullName : "-",
                                  emailId       : a.emailId ? a.emailId : "-",
                                  mobileNumber    : a.mobileNumber ? a.mobileNumber : "-", 
                                  status          : a.status ? a.status : "-",  
                                  roles       : a.roles ? a.roles : "-",
                                  checked        : false,
                        }
                      })
                      this.setState({
                            completeDataCount : res.data.length,
                            tableData     : tableData,          
                          },()=>{
                            // console.log('tableData', this.state.tableData);
                          })
                    })
                    .catch((error)=>{
                      // console.log("error = ",error);
                      // alert("Something went wrong! Please check Get URL.");
                        if(error.message === "Request failed with status code 401")
                          {
                               swal("Your session is expired! Please login again.","", "error");
                               this.props.history.push("/");
                          }
                    });

                  }


                }).catch((error)=>{ 

                // console.log("error = ",error);

                  if(error.message === "Request failed with status code 401")
                    {
                         swal("Your session is expired! Please login again.","", "error");
                         this.props.history.push("/");
                    }
              });

           }  

           this.setState({
            unCheckedUser : []
        },()=>{
          console.log("unCheckedUser",this.state.unCheckedUser);
        })
            break;

          case 'remove':


           for(var i=0;i< checkedUsersList.length;i++)
          {
            var selectedId = checkedUsersList[i];
            var formValues ={
              userID : selectedId,
              roles   : role,
            }

             axios
              .post('/api/users/post/roledelete/',formValues)
              .then(
                (res)=>{
                  // console.log('res', res);
                  swal("Assigned Role Removed Successfully","","success");
                  checkedUsersList = null;

                      // update table here
                      var data = {
                      "startRange"        : this.state.startRange,
                            "limitRange"        : this.state.limitRange, 
                    }
                    axios.post('/api/users/userslist', data)
                    .then( (res)=>{      
                      // console.log("herer",res);
                      var tableData = res.data.map((a, i)=>{
                        return {
                          _id       : a._id,
                          fullName        : a.fullName ? a.fullName : "-",
                                  emailId       : a.emailId ? a.emailId : "-",
                                  mobileNumber    : a.mobileNumber ? a.mobileNumber : "-", 
                                  status          : a.status ? a.status : "-",  
                                  roles       : a.roles ? a.roles : "-",
                                  checked         : false,
                        }
                      })
                      this.setState({
                            completeDataCount : res.data.length,
                            tableData     : tableData,          
                          },()=>{
                            // console.log('tableData', this.state.tableData);
                          })
                    })
                    .catch((error)=>{
                      // console.log("error = ",error);
                      // alert("Something went wrong! Please check Get URL.");
                        if(error.message === "Request failed with status code 401")
                        {
                             swal("Your session is expired! Please login again.","", "error");
                             this.props.history.push("/");
                        }
                    });

                }).catch((error)=>{ 

                // console.log("error = ",error);
                  if(error.message === "Request failed with status code 401")
                    {
                         swal("Your session is expired! Please login again.","", "error");
                         this.props.history.push("/");
                    }
              });

           }  

            break;
        }
        this.setState({
            unCheckedUser : []
        },()=>{
          console.log("unCheckedUser",this.state.unCheckedUser);
        })
      }else{
        // this.refs.userListDropdown.value = '-';
        // swal({
      //        title:'abc',
      //        text:"Please select atleast one user."
      //      });
      }
      // {this.usersListData()}
  }

  selectedRole(event){
        event.preventDefault();
        var selectedValue        = this.refs.roleListDropdown.value;
