import React, { Component } 		from 'react';
// import { render } 					from 'react-dom';
// import TrackerReact 				from 'meteor/ultimatejs:tracker-react';
import swal                       from 'sweetalert';

  import UMaddRoles 					from './UMaddRoles.jsx';
// import { UserManagementMaster }  	from '/imports/admin/userManagement/UM/UserManagementMaster.js';
/*import './userManagement.css';
*/
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import 'font-awesome/css/font-awesome.min.css';
import 'bootstrap/js/modal.js';


export default class UMRolesList extends Component {
	constructor(){
		super();
		this.state = {
				allPosts : [],

		}
	}

	componentDidMount(){
		this.getdata();



	}
  		getdata(){
  			axios
			.get('/api/roles/list')
			.then(
				(res)=>{
					// console.log('res', res);
					const postsdata = res.data;
					// console.log('postsdata',postsdata);
					this.setState({
						allPosts : postsdata,
					});
				}
			)
			.catch((error)=>{

				console.log("error = ",error);
				// alert("Something went wrong! Please check Get URL.");
				 });		
		}

  		deleteRole(event){
  			event.preventDefault();
			var id = event.target.id;
			// console.log("id",id);
			const token = '';
			const url = '/api/roles/'+id ;
			const headers = {
				    "Authorization" : token,
				    "Content-Type" 	: "application/json",
				};

				axios({
					method: "DELETE",
					url : url,
					headers: headers,
					timeout: 3000,
					data: null,
				})

				.then((response)=> {
			    	//console.log('delete response',response);
			    	 swal("Role deleted successfully");
			    	 		this.getdata();
				}).catch((error)=> {
				    // handle error
				    console.log(error);
				});
  		}

  		editRole(event){
			var id = event.target.id;
			// console.log("userid-----------------------------------------",id, this.refs["roleName"+id].value);
			var formvalues = {
				"id"		: id,
				"role"		: this.refs["roleName"+id].value,
			}
			console.log("formvalues",formvalues);
				axios.put('/api/roles', formvalues)
				.then((response)=> {		
					swal("Role updated successfully",);	
					 this.getdata();
					 this.props.history.push('/umroleslist');	
					// console.log('response --==',response);
				})
				.catch(function (error) {
					console.log('error============',error);
			});


  		}
  		handleedit(event){
  			event.preventDefault();
  			this.setState({

  			});

  		}
	render(){

       return(
			<div className="">
				<div className=""></div>
				<section className="">
			        <div className="">
			          	<div className="">
	                        <div className="">
	                            <div className="box col-lg-12 col-md-12 col-xs-12 col-sm-12">
	                            	<div className=" col-lg-1 col-md-1 col-xs-1 col-sm-1 box-header with-border text-center">
                                         <h4 className="weighttitle"><a href="/UMListOfUsers"><i className="cursorpointer fa fa-chevron-circle-left"></i></a></h4>
                                    </div>
                                    <div className=" col-lg-11 col-md-11 col-xs-11 col-sm-11 box-header with-border">
                                         <h4 className="weighttitle">List of Roles</h4>
                                    </div>
									<div className="col-lg-12 col-md-12 col-sm-12 col-xs-12 addRolesInWrap">
											<UMaddRoles getdata={this.getdata.bind(this)} />
										<div className="table-responsive col-lg-12 col-md-12 col-sm-12 col-xs-12">
											<table className="table iAssureITtable-bordered table-striped table-hover">
